package ar.edu.unju.fi.ejercicio2;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Declaracion y asignacion de variables
		String pais = "Argentina";
		short edad = 22;
		float hEdificio = (float)150.4;
		float precio = (float)120.5;
		long telefono = 380658925l;
		double coseno = Math.cos(0.5);
		
		//Muestra de variables por consola
		System.out.println("El nombre de un país: " + pais);
		System.out.println("La edad de una persona: " + edad);
		System.out.println("La altura de un edificio: " + hEdificio);
		System.out.println("El precio de un producto de supermercado: " + precio);
		System.out.println("Número de teléfono: " + telefono);
		System.out.println("El cálculo de coseno de 0.5: "+ coseno);
		
		
	}

}
